mapper.select:
SELECT 
    sco.*
FROM 
    sim_card_operation sco


        AND sco.iccid LIKE concat('%', #{params.iccid}, '%')


        AND sco.card_id = #{params.cardId}


        AND sco.operation_type = #{params.operationType}


        AND sco.operation_type IN

            #{type}



        AND sco.operator_user_id = #{params.operatorUserId}


        AND sco.operation_time >= #{params.startTime}


        AND sco.operation_time <= #{params.endTime}


        AND sco.result_status = #{params.resultStatus}


        AND sco.stock_out_org_id = #{params.stockOutOrgId}


        AND sco.stock_in_org_id = #{params.stockInOrgId}


ORDER BY sco.operation_time DESC
